from __future__ import annotations

from typing import Any

__version__ = "2.0.22"


def __go(lcls: Any) -> None:
    from . import util as _sa_util

    _sa_util.preloaded.import_prefix("sqlalchemy")

    from . import exc

    exc._version_token = "".join(__version__.split(".")[0:2])


__go(locals())
